INSERT INTO `news` (`id`, `tag`, `main_title`, `writter`, `title_img`, `text1`, `img1`, `text2`, `img2`, `text3`) VALUES
('xiaomi mi max 4', 'تکنولوژی', 'می میکس ۴ شیائومی احتمالا با دوربین زیر نمایشگر از راه می‌رسد', 'جواد تاجی ، 17 تیر 1400', 'https://digiato.com/wp-content/uploads/2021/07/gsmarena_000-2-1024x640.jpg', 'به نظر می‌رسد شیائومی سرانجام قصد دارد از اولین گوشی خود با فناوری دوربین زیر نمایشگر رونمایی کند. طبق گفته منابع آگاه حوزه تکنولوژی، احتمالا می میکس ۴ که از دوربین زیر نمایشگر کاملا نامرئی بهره می‌برد. \r\nشیائومی برای چندین سال روی فناوری دوربین زیر نمایشگر کار می‌کند اما هنوز هیچ کدام از گوشی‌های خود را با استفاده از این فناوری عرضه نکرده است. «می ۱۰ اولترا» قرار بود اولین گوشی شیائومی با دوربین زیر نمایشگر باشد اما چنین چیزی برای این گوشی و «می ۱۱ اولترا» اتفاق نیفتاد. اما اکنون به گفته Ice Universe شیائومی سرانجام متوجه شده است که چگونه دوربین زیر نمایشگر را به صورت کامل مخفی کند و احتمالا گوشی می میکس ۴ اولین دستگاه آن‌ها با این فناوری باشد. طبق این گزارش، زمانی که کاربران از دوربین گوشی خود استفاده نمی‌کنند، با چشم غیر مسلح نمی‌توان موقعیت آن را تشخیص داد', 'https://digiato.com/wp-content/uploads/2021/07/gsmarena_001-4.jpg', 'HoiINDI رندرهایی را از پنل پشتی دستگاه به اشتراک گذاشته است که نمایشگر ثانویه کنار ماژول دوربین اصلی آن را نشان می‌دهد و البته به نظر می‌رسد نمایشگر ثانویه این گوشی تاحدی از نمایشگر می ۱۱ اولترا بزرگ‌تر است.', 'https://digiato.com/wp-content/uploads/2021/07/gsmarena_005-2.jpg', 'شیائومی ممکن است تا آخر تابستان از گوشی می میکس ۴ در کنار رابط کاربری MIUI 13 رونمایی کند. انتظار می‌رود این موبایل جدید به همراه چیپست کوالکام اسنپدراگون ۸۸۸ و باتری ۴۵۰۰ میلی آمپر ساعتی با پشتیبانی از شارژ سریع سیمی ۱۲۰ وات عرضه شود. همچنین پیش از این گزارش شده بود که گوشی می میکس ۴ فقط در یک نسخه وارد بازار می‌شود که دارای سنسور دوربین زیر نمایشگر است و قیمت این گوشی از می ۱۱ اولترا نیز بیشتر خواهد بود.\r\n\r\nهنوز برای نتیجه‌گیری درباره مشخصات فنی می میکس ۴ زود است و نمی‌توانیم با قطعیت در مورد مشخصات آن صحبت کنیم. اما به نظر می‌رسد در هفته‌های پیش رو گزارش‌های بیشتری درباره می میکس بعدی منتشر شود. همچنین طرح مفهومی جدیدی که از می ۱۲ شیائومی به اشتراک گذاشته شده بود، طراحی گوشی دیگر این شرکت را با دوربین زیر نمایشگر مشخص می‌کرد.'),
('xaiomi fold', 'تکنولوژی', 'شیائومی روی گوشی تاشو جدیدی با زبان طراحی می میکس آلفا کار می‌کند', 'تینا پورشاهید ، 16 تیر 1400', 'https://digiato.com/wp-content/uploads/2021/07/gsmarena_002-3-1024x676.jpg', 'مستندات مربوط به پتنت شیائومی از تلاش این شرکت برای توسعه گوشی تاشو جدیدی با طراحی وام گرفته از گوشی‌های هوشمند می میکس آلفا و می میکس فولد خبر می‌دهد.\r\n\r\nاواخر سال ۲۰۱۹ (مهرماه ۱۳۹۸) شیائومی از گوشی تاشو می میکس آلفا رونمایی کرد و اوایل سال جاری میلادی نیز اولین گوشی تاشو خود با نام می میکس فولد را به بازار عرضه کرد. اکنون به نظر می‌رسد چینی‌ها روی توسعه دستگاهی با طراحی متفاوت کار می‌کنند که ترکیبی از این دو گوشی تاشو را به نمایش می‌گذارد.\r\n\r\nدستگاه جدید قرار است طراحی ترکیبی دو گوشی تاشو می میکس آلفا و فولد را ارائه دهد، با این تفاوت که نمایشگر ثانویه، بخشی از نمایشگر اصلی دستگاه را تشکیل می‌دهد', 'https://digiato.com/wp-content/uploads/2021/07/gsmarena_001-1.jpg', 'طراحی قسمتی از دستگاه که ضخامت بیشتری دارد و دوربین‌ها را در خود جای می‌دهد، مشابه نسخه آلفا است. «لی جون»، مدیرعامل شیائومی معتقد است که توسعه دستگاهی با نمایشگر تاشو که بتواند گوشی را کامل بپوشاند در تولید چالش‌های مختلفی دارد. در واقع طراحی گوشی تاشو پیچیدگی‌های خاص خودش را دارد و حالا اگر نمایشگر بخواهد قابل انعطاف هم باشد، به این پیچیدگی‌ها اضافه می‌شود', 'https://digiato.com/wp-content/uploads/2021/07/photo_2019-09-24_12-03-25.jpg', 'براساس اخبار منتشر شده از سوی منابع آگاه، ظاهرا شیائومی در حال آماده‌سازی گوشی تاشو جدیدی است و قصد دارد آن را در فصل چهارم سال جاری میلادی به بازار عرضه کند. اخبار غیررسمی مختلفی در مورد مشخصات این دستگاه به اشتراک گذاشته شده و به نظر می‌رسد دستگاه مذکور دارای دو نمایشگر باشد که یکی از آنها با نرخ تازه سازی ۱۲۰ هرتز توسط سامسونگ و نمایشگر دوم با نرخ تازه سازی ۹۰ هرتز توسط Visionox تامین خواهد شد.\r\n\r\nبا توجه به اینکه ساخت چنین دستگاهی دشواری‌های خاص خودش را دارد، انتظار می‌رود قیمت بالایی برای آن تعیین شود. در نهایت باید دید شیائومی گوشی تاشو جدید خود را با چه مشخصات و طراحی در دسترس کاربران قرار می‌دهد.'),
('k40 ultra', 'موبایل', 'ردمی K40 اولترا با چیپ دیمنسیتی ۱۲۰۰ به مصاف وان پلاس نورد ۲ می‌رود', 'ایمان صاحبی ، 15 تیر 1400', 'https://digiato.com/wp-content/uploads/2021/02/Redmi_K40_25.02-w1200.jpg', 'انتظار داریم شیائومی در نیمه دوم سال از حداقل ۱۳ دستگاه جدید رونمایی کند. دیروز در گزارشی به نام چند محصول که اواخر مرداد یا اوایل شهریور معرفی می‌شوند، اشاره شد. حالا در گزارشی تازه اعلام شده که ردمی K40 اولترا با چیپست دیمنسیتی ۱۲۰۰ در راه است.\r\n\r\nبه گزارش Xiaomiui، ردمی به‌زودی از دومین تلفن هوشمند خود با دیمنسیتی ۱۲۰۰ پرده برمی‌دارد. این دستگاه احتمالا با نام ردمی K40 اولترا به بازار می‌آید و رقیب مستقیم وان پلاس نورد ۲ خواهد بود. یک منبع آگاه دیگر هم موبایلی را از شیائومی رویت کرده که احتمالا همان ردمی K40 اولتراست و از نمایشگر ۱۲۰ هرتزی، دوربین اصلی ۶۴ مگاپیکسلی، باتری ۵۰۰۰ میلی‌آمپر ساعتی و فناوری شارژ سریع ۶۷ واتی بهره می‌برد.\r\n\r\nطبق اطلاعات این منبع، ردمی K40 اولترا از چیپست مدیاتک MT689x استفاده می‌کند. اگر نمی‌دانید باید به شما بگوییم که دیمنسیتی ۱۲۰۰ از شماره مدل MT6893 برخوردار است. با این حال، هنوز نمی‌توانیم با قطعیت اعلام کنیم که هر دو منبع در حال صحبت درباره یک دستگاه هستند.', 'https://digiato.com/wp-content/uploads/2021/04/Redmi-K40-Gaming-Enhanced-Edition.jpg', 'حالا باید منتظر باشیم تا این گوشی هوشمند هم مثل سایر محصولات شیائومی پیش از عرضه از کد منبع MIUI سر در بیاورد و اطلاعات آن را در مراجع صدور مجوز مشاهده کنیم. از سوی دیگر، دیروز در گزارشی اعلام شد که وان پلاس نورد ۲ هم احتمالا در اوایل مرداد ماه معرفی می‌شود. این گوشی باید نمایشگری ۶.۴۳ اینچی از نوع امولد با نرخ نوسازی ۹۰ هرتزی، دیمنسیتی ۱۲۰۰، حداکثر ۱۲ گیگابایت رم و ۲۵۶ گیگابایت حافظه داخلی داشته باشد.\r\n\r\nردمی همچنین در حال کار روی گوشی‌های پرچمدار بعدی خود است و اگرچه تاریخ دقیقی برای عرضه ردمی K50 اعلام نشده، ولی مدیرکل این برند چندی پیش از کاربران خواسته بود که پیشنهادهای خود را برای این سری اعلام کنند. در نتیجه حدس زده می‌شود که سری ردمی K50 تا پیش از پایان سال از راه برسد.', 'null', 'null'),
('MIUI 13', 'تکنولوژی', 'رابط کاربری MIUI 13 احتمالا تا دو ماه دیگر با شیائومی می میکس ۴ از راه می‌رسد', 'محمد قریشی  ، 5 تیر 1400', 'https://cdn01.zoomit.ir/2021/7/miui-13.jpg', 'شیائومی روی رابط کاربری MIUI 13 کار می‌کند و طبق گفته یکی از مدیران بالارده این کمپانی، برای معرفی آن در ماه آگوست (مرداد و شهریور) آماده می‌شود و احتمالا همراه با آن، از گوشی می میکس ۴ هم رونمایی خواهد شد.\r\n\r\nنسخه بعدی رابط کاربری شیائومی اکنون در فاز آزمایشی قرار دارد و به احتمال زیاد در ماه آگوست معرفی می‌شود. پس از رونمایی، رابط کاربری MIUI 13 ابتدا در اختیار کاربران چینی قرار می‌گیرد و سپس در ماه‌های آینده، کاربران در بازار جهانی می‌توانند جدیدترین دستاورد شیائومی را تجربه کنند.\r\n\r\nدرباره این رابط کاربری جدید اطلاعات رسمی منتشر نشده و نمی‌دانیم با چه سبک طراحی روبه‌رو می‌شویم. علاوه بر این، از زبان طراحی که شیائومی انتخاب کرده هم اطلاعی نداریم.\r\n\r\nبا توجه به اینکه اندروید ۱۲ هنوز در فاز بتا قرار دارد و تا پاییز نسخه نهایی‌اش در اختیار کاربران قرار نمی‌گیرد، رابط کاربری MIUI 13 به احتمال زیاد مبتنی بر اندروید ۱۱ خواهد بود. پس از اینکه اندروید ۱۲ منتشر شود، شیائومی رابط کاربری جدیدش را با سیستم عامل موبایل جدید گوگل سازگار خواهد کرد.', 'https://fdn.gsmarena.com/imgroot/news/21/05/xiaomi-miui-13-date/-1200/gsmarena_001.jpg', 'انتظار می‌رود MIUI 13 برای محصولات زیادی منتشر شود. گزارش‌ها از انتشار این رابط کاربری جدید برای تمام دستگاه‌های شیائومی با عمر دو سال خبر می‌دهند، بنابراین کاربران زیادی می‌توانند آن را تجربه کنند.', 'https://mihamrah.com/wp-content/uploads/2021/05/MIUI-13-6.jpg', 'مدت‌هاست گزارش‌هایی درباره توسعه گوشی می میکس ۴ توسط شیائومی منتشر می‌شود. این گوشی هوشمند که به مشخصات فنی قدرتمند و جدیدترین فناوری‌ها مجهز خواهد شد، در رده محصولاتی مانند می ۱۱ اولترا قرار می‌گیرد. این احتمال وجود دارد که این محصول جذاب همراه با MIUI 13 در ماه آگوست از راه برسد و به اولین گوشی چینی‌ها با رابط جدید شیائومی تبدیل شود.\r\n\r\nگوشی‌های سری می میکس همواره با جدیدترین قطعات سخت افزاری و فناوری‌های مدرن وارد بازار شده‌اند که برای مثال می‌توان به حاشیه‌های بسیار کم اطراف نمایشگر یا مکانیزم کشویی دوربین در نسل‌های قبلی اشاره کرد. حالا گزارش‌ها از تجهیز نسل جدید این سری به دوربین سلفی زیر نمایشگر خبر می‌دهند و البته انتظار می‌رود قادر به پشتیبانی از باند فوق وسیع هم باشد. این دستگاه احتمالا با باتری ۵۰۰۰ میلی آمپر ساعتی از راه می‌رسد.'),
('android 12 xiaomi', 'تکنولوژی', 'این گوشی‌های شیائومی، ردمی و پوکو احتمالا اندروید ۱۲ را دریافت می‌کنند', 'محمد قریشی ، 14 تیر 1400', 'https://fdn.gsmarena.com/imgroot/news/21/06/android-12-beta-most-downloaded/-344x215/gsmarena_000.jpg', 'هفته‌ها از انتشار بتای توسعه‌دهندگان اندروید ۱۲ می‌گذرد، با این حال چندین ماه تا انتشار نسخه نهایی‌اش فاصله داریم. با انتشار این سیستم عامل از سوی گوگل، شرکت‌ها آن را در اختیار کاربران محصولاتشان قرار می‌دهند. حالا لیستی منتشر شده که دستگاه‌های احتمالی شیائومی و زیرمجموعه‌هایش که اندروید ۱۲ را دریافت می‌کنند، مشخص می‌کند.\r\n\r\nاگرچه شاید شیائومی با سرعت بالا و در سریع‌ترین زمان ممکن جدیدترین نسخه اندروید را برای دستگاه‌هایش منتشر نکند، اما این کمپانی معمولا از محصولات زیادی پشتیبانی به عمل می‌آورد. این کمپانی هنوز فهرستی از دستگاه‌های دریافت‌کننده اندروید ۱۲ منتشر نکرده، اما حساب توییتری Xiaomi با استفاده از منابع داخلی شیائومی و توسعه‌دهندگان MIUI، لیستی از آن‌ها منتشر کرده است.\r\n\r\nطبق این فهرست، کاربران تمام پرچمدارهای سری Mi در سال‌های ۲۰۲۰ و ۲۰۲۱ جدیدترین سیستم عامل موبایل گوگل را دریافت خواهد کرد. علاوه بر این، گوشی‌های سری ردمی نوت ۹، ردمی نوت ۱۰، ردمی K30 و K40 هم به اندروید ۱۲ دسترسی پیدا می‌کنند.', 'https://digiato.com/wp-content/uploads/2021/07/mi-3.jpg', 'در لیست بالا می‌توانید این دستگاه‌ها را مشاهده کنید، البته در آن محصولاتی که اندروید ۱۲ را دریافت نمی‌کنند هم به چشم می‌خورند. برای مثال کاربران محصولات سری می ۹، می ۹T، می نوت ۱۰ و ۱۰ پرو، سری ردمی نوت ۸ و ردمی ۹ ظاهرا نباید منتظر دریافت اندروید ۱۲ باشند.\r\nبه احتمال بسیار زیادی دستگاه‌های زیر اندروید ۱۲ را دریافت خواهند کرد:\r\n\r\nشیائومی می ۱۱ اولترا\r\nشیائومی می ۱۱ پرو\r\nشیائومی می ۱۱\r\nشیائومی می ۱۱i و ۱۱X\r\nشیائومی می ۱۰ اولترا\r\nشیائومی می ۱۰ پرو\r\nشیائومی می ۱۰S\r\nشیائومی می ۱۰\r\nشیائومی می ۱۰i\r\nشیائومی می ۱۰ لایت\r\nشیائومی می ۱۰ لایت زوم\r\nشیائومی می ۱۰T\r\nشیائومی می ۱۰T پرو\r\nشیائومی می ۱۰T لایت\r\nسری ردمی K30 و K40\r\nپوکو F2 پرو\r\nپوکو F3\r\nسری بلک شارک ۳ و ۴\r\nهمانطور که در ابتدا گفتیم، با یک فهرست رسمی روبه‌رو نیستیم و احتمال تغییر در محصولات آن وجود دارد.', 'null', 'null'),
('mi max flip', 'موبایل', 'شیائومی احتمالا برای رقابت با گلکسی زد فلیپ، روی گوشی می میکس فلیپ کار می‌کند', 'محمد قریشی ، 12 تیر 1400', 'https://digiato.com/wp-content/uploads/2021/07/Xiaomi-Mi-Mix-Flip-1024x633.jpg', 'بازار گوشی‌های تاشو در آینده میزبان محصولات جدیدی خواهد بود که احتمالا یکی از آن‌ها، شیائومی می میکس فلیپ باشد که نسبت به می میکس فولد ابعاد کوچک‌تری دارد و با گلکسی زد فلیپ سامسونگ رقابت می‌کند.\r\n\r\nشیائومی می میکس فولد اولین گوشی تاشو چینی‌ها بود که از طراحی مشابه با گلکسی زد فولد ۲ سامسونگ بهره می‌برد. حالا ظاهرا شیائومی می‌خواهد یک گوشی تاشو جدید معرفی کند که این محصول هم طراحی مشابه با دیگر موبایل تاشو کره‌ای‌ها دارد.\r\n\r\nطبق گزارشی جدید، شیائومی اخیرا پتنت یک گوشی تاشو را به ثبت رسانده که با وجود شباهت کلی به گلکسی زد فلیپ، قابلیت‌های متفاوتی نسبت به آن دارد و احتمالا با قیمت پایین‌تری هم در اختیار خریداران قرار می‌گیرد.\r\n\r\nطبق تصاویر این پتنت، گوشی تاشو چینی‌ها از حفره بیضی شکل برای میزبانی دوربین سلفی بهره می‌برد. روی پنل پشتی آن شاهد قرارگیری یک ماژول دایره‌ای هستیم که درون آن سه سنسور دوربین به چشم می‌خورد.', 'https://digiato.com/wp-content/uploads/2021/07/Xiaomi-Mi-Mix-Flip-1.jpg', 'در بخش پایینی دستگاه پورت USB Type-C به همراه حفره‌های اسپیکر قرار گرفته‌اند. در حال حاضر اطلاعاتی درباره مشخصات و قیمت این دستگاه منتشر نشده و تنها با یک پتنت روبه‌رو هستیم، بنابراین شاید اصلا شیائومی برنامه‌ای برای عرضه چنین محصولی نداشته باشد.\r\n\r\nبا وجود اینکه ثبت یک پتنت تضمینی برای عرضه دستگاهی جدید یا استفاده از فناوری‌های موجود در آن در محصولات در آینده نزدیک نیست، اما با توجه به وضعیتی که بازار گوشی‌های تاشو تجربه می‌کند، احتمالا می میکس فلیپ معرفی خواهد شد.\r\n\r\nاز سال آینده میلادی احتمالا شاهد ورود گوشی‌های تاشو جدیدی به بازار باشیم و شیائومی هم به عنوان یکی از بزرگترین تولیدکنندگان گوشی‌های هوشمند، باید حضور فعال‌تری در این بازار داشته باشد.\r\n\r\nبا توجه به اینکه گفته می‌شود سامسونگ می‌خواهد قیمت نسل جدید گوشی‌های تاشوی خود یعنی گلکسی زد فلیپ ۳ و زد فولد ۳ را نسبت به نسل گذشته کاهش دهد، رقابت در این بخش از بازار دشوارتر خواهد شد. بنابراین شیائومی می‌تواند با قیمت پایین‌تر، حضور قدرتمندی در بازار داشته باشد.', 'null', 'null'),
('xiaomi mi 12', 'تکنولوژی', 'طراحی مفهومی می ۱۲ شیائومی از دوربین زیر نمایشگر خبر می‌دهد', 'جواد تاجی ،  21 خرداد 1400', 'https://digiato.com/wp-content/uploads/2021/06/1617998859_Meet-the-Xiaomi-Mi-12-Ultra-First-characteristics-and-images.jpg', 'طرح مفهومی به اشتراک گذاشته شده از می ۱۲ شیائومی،‌ طراحی پنل جلویی و پشتی دستگاه را به خوبی نشان می‌دهد. احتمال دارد چینی‌ها موبایل جدید خود را با دوربین زیر نمایشگر و مجهز به چهار دوربین در پنل پشتی به بازار عرضه کنند.\r\n\r\nمی ۱۲ شیائومی احتمالا اواخر سال جاری یا اوایل سال آینده وارد بازار می‌شود. تصاویر مفهومی جدیدی از این موبایل به اشتراک گذاشته شده که نمایشگر دستگاه را بدون بریدگی نشان می‌دهد.\r\n\r\nطراحی دستگاهی که در تصاویر ارائه شده دیده می‌شود تا حدی شبیه به «می ۱۱» شیائومی است. می ۱۲ از ماژول دوربین مشابهی بهره می‌برد اما تعداد سنسورهای آن افزایش پیدا کرده است. قسمت جلوی آن نیز تقریبا مشابه بوده و فقط بریدگی روی نمایشگر نیز حذف شده است', 'https://digiato.com/wp-content/uploads/2021/06/Xiaomi-Mi-12-concept-1.jpeg', 'گوشی می ۱۲ همانطور که در این تصاویر نیز مشخص است از دوربین زیرنمایشگر بهره می‌برد. تقریبا می‌توان گفت که در اطراف نمایشگر هیچ حاشیه‌ای دیده نمی‌شود و لبه‌های آن دارای انحنا هستند. لبه‌های پنل پشتی موبایل نیز برای حفظ تناسب با نمایشگر دارای انحنا هستند. در پنل پشتی دستگاه نیز ماژول دوربین چهارگانه و دو فلاش LED دیده می‌شود.\r\n\r\nنام برند تجاری شیائومی نیز در پشت دستگاه دیده می‌شود و طراح به دو صورت عمودی و افقی آن را به تصویر کشیده است. همچنین دکمه‌های فیزیکی گوشی که احتمالا شامل دکمه پاور و تنظیم صدا می‌شود در سمت راست آن قرار گرفته است.\r\n\r\nگوشی‌های شیائومی طرفداران خودش را دارد و گفته می‌شود از زمان عرضه سری ردمی نوت ۱۰ در هند، شیائومی حدود ۲ میلیون دستگاه به ارزش بیش از ۴۰۰ میلیون دلار فروخته است. سری ردمی نوت در سراسر جهان و به ویژه در هند محبوبیت بسیار زیادی پیدا کرده است. به گفته «مانو کومار جین» مدیر شیائومی هند اگر ردمی یک برند مستقل محسوب می‌شد، به تنها رتبه سوم یا چهارم بازار هند را کسب می‌کرد. همچنین در یک ماه و نیم از زمان عرضه خود تاکنون بیش از ۴۰ میلیون دلار گوشی از سری می ۱۱ ردمی به فروش رسیده است.', 'null', 'null'),
('Xiaomi Redmi note 10 pro 5g', 'موبایل', 'ردمی نوت ۱۰ پرو 5G با چیپ دیمنسیتی ۱۱۰۰ و نمایشگر ۱۲۰ هرتزی معرفی شد', 'محمد قریشی ، 5 خرداد 1400 ', 'https://digiato.com/wp-content/uploads/2021/05/Redmi-Note-10-Pro-5G-1024x576.jpg', 'ردمی در چین از جدیدترین محصولاتش رونمایی کرد. گوشی ردمی نوت ۱۰ پرو ۵G تفاوت‌هایی با نسخه ۴G در بازار جهانی دارد و از مشخصات فنی قدرتمندتری بهره می‌برد.\r\n\r\nاین گوشی هوشمند با ردمی نوت ۱۰ پرو و پرو مکس که در بازار جهانی فروخته می‌شوند، از طراحی تقریبا متفاوتی بهره می‌برد و مشخصاتش هم فرق می‌کند. برای مثال در این دستگاه دیگر خبری از نمایشگر امولد نیست و ظاهرا به علت پشتیبانی از شبکه ۵G، کاربران باید به پنل LCD رضایت دهند.\r\n\r\nنمایشگر ۶.۶ اینچی این دستگاه رزولوشن فول اچ‌دی پلاس و رفرش ریت ۱۲۰ هرتز دارد و از نرخ نمونه‌برداری لمسی ۲۴۰ هرتز پشتیبانی می‌کند. در مرکز بخش بالایی صفحه نمایش حفره‌ای برای میزبانی دوربین سلفی به چشم می‌خورد. ردمی ۶ حالت برای رفرش ریت این پنل درنظر گرفته که با توجه به محتوای در حال نمایش تغییر می‌کند و کمترین آن‌ها، ۳۰ هرتز است.', 'https://digiato.com/wp-content/uploads/2021/05/Redmi-Note-10-Pro-5G-1.jpg', 'این نمایشگر گاموت رنگی DCI-P3 را پوشش می‌دهد و حداکثر روشنایی ۴۵۰ نیت دارد. یکی از تفاوت‌های دیگر این دستگاه با نسخه موجود در بازار جهانی، استفاده از محافظ قدرتمند گوریلا گلس ویکتوس روی نمایشگر است، محافظی که اکثر پرچمداران اندرویدی از آن بهره می‌برند.\r\n\r\nدر این دستگاه دیگر خبری از چیپ اسنپدراگون نیست و مانند ردمی نوت ۱۰ ۵G با چیپ مدیاتک روبه‌رو هستیم که البته قدرت بالاتری نسبت به برادرش دارد. ردمی نوت ۱۰ پرو ۵G به تراشه دیمنسیتی ۱۱۰۰ مجهز شده و حداکثر با ۸ گیگابایت رم LPDDR4x و ۲۵۶ گیگابایت حافظه داخلی UFS 3.1 وارد بازار می‌شود.\r\n\r\nپنل پشتی این دستگاه از یک ماژول دوربین سه‌گانه میزبانی می‌کند که فاقد سنسور ۱۰۸ مگاپیکسلی است، سنسوری که اخیرا موجب شکست آیفون SE 2020 توسط ردمی نوت ۱۰ پرو در بررسی DxOMark شده است. این گوشی از سنسور اصلی ۶۴ مگاپیکسلی به همراه دوربین ۸ مگاپیکسلی اولترا واید و ۲ مگاپیکسلی ماکرو بهره می‌برد. کاربران برای ثبت تصاویر سلفی هم کاربران به دوربین ۱۶ مگاپیکسلی دسترسی دارند.', 'https://digiato.com/wp-content/uploads/2021/05/Redmi-Note-10-Pro-5G-2.jpg', 'این گوشی که ضخامت و وزنی برابر ۸.۹ میلی‌متر و ۱۹۳ گرم دارد، به باتری ۵۰۰۰ میلی آمپر ساعتی با پشتیبانی از فناوری شارژ سریع ۶۷ واتی مجهز شده که باعث شارژ کامل آن در مدت ۴۲ دقیقه می‌شود.\r\n\r\nاز سایر مشخصات این دستگاه می‌توان به پورت USB Type-C، قرارگیری سنسور اثر انگشت روی فریم، بلوتوث ۵.۲، وای فای ۶ و NFC اشاره کرد. در این موبایل خبری از جک ۳.۵ میلی‌متری هدفون و درگاه کارت حافظه نیست. ردمی نوت ۱۰ پرو ۵G همچنین از اندروید ۱۱، رابط کاربری MIUI 12.5، اسپیکر استریو JBL با پشتیبانی از دالبی اتموس و ۳ میکروفن بهره می‌برد.'),
('Redmi note 10 pro camera', 'تکنولوژی', 'دوربین ردمی نوت ۱۰ پرو در بررسی DxOMark بالاتر از آیفون SE ۲۰۲۰ قرار گرفت', 'محمد قریشی ، 4 خرداد 1400', 'https://digiato.com/wp-content/uploads/2021/05/Digipic-RedmiNote10Pro-09-1024x683.jpg', 'ردمی نوت ۱۰ پرو یکی از بهترین گوشی‌های شیائومی محسوب می‌شود که در کنار قیمت مقرون به صرفه، مشخصات فنی مناسبی هم دارد. یکی از قابلیت‌های جذاب این دستگاه، دوربین اصلی ۱۰۸ مگاپیکسلی است. حالا DxOMark دوربین ردمی نوت ۱۰ پرو را مورد بررسی قرار داده که نتیجه‌اش، کسب امتیاز بالاتر نسبت به محصولاتی مانند آیفون SE ۲۰۲۰ و گلکسی زد فلیپ است.\r\n\r\nاین گوشی هوشمند به امتیاز کلی ۱۰۶ دست پیدا کرده که آن را در رده ۷۲ لیست DxOMark قرار می‌دهد، البته در محدوده قیمتی ۲۰۰ تا ۳۹۹ دلار، سومین موبایل از نظر امتیاز دوربین محسوب می‌شود. امتیاز ۱۰۶ این دستگاه بالاتر از امتیاز ۱۰۳ آیفون SE ۲۰۲۰ و ۱۰۵ گلکسی زد فلیپ است.\r\n\r\nگوشی ردمی در بخش عکاسی و فیلمبرداری به ترتیب به امتیازهای ۱۱۱ و ۹۵ دست پیدا کرده و توانسته برای بخش زوم، امتیاز ۵۲ را بدست آورد. از آنجایی که این دستگاه فاقد دوربین تله‌فوتو است، کسب امتیاز پایین در بخش زوم چندان تعجب‌برانگیز نیست.\r\n\r\nبه گفته DxOMark این دستگاه در تصاویر پرتره در فضای باز جزئیات زیادی را ثبت می‌کند، نوردهی در فیلمبرداری دقیق است و نویز به خوبی در ویدیوها کنترل می‌شوند. علاوه بر این، محصول ردمی در تخمین عمق هم عملکرد دقیقی از خودش نشان می‌دهد.', 'https://digiato.com/wp-content/uploads/2021/05/Redmi-Note-10-Pro-DxOMark.jpg', 'از ضعف‌های دوربین ردمی نوت ۱۰ پرو می‌توان به دامنه دینامیکی پایین، جزئیات کم در هنگام زوم و همچنین وجود رنگ سبز در تصاویر در محیط‌های آزاد اشاره کرد.\r\n\r\nبه گفته DxOMark، دوربین ۱۰۸ مگاپیکسلی این دستگاه در شرایط نوری مناسب، تصاویر باکیفیتی می‌گیرد. در بخش فیلم‌برداری این دستگاه توانسته به امتیاز بالاتری نسبت به نسخه اگزینوس گلکسی S21 و S21 پلاس دست کند. این گوشی‌های هوشمند در بخش فیلم‌برداری امتیاز ۹۳ را بدست آورده‌اند.', 'null', 'null'),
('Google pixel 6 ', 'تکنولوژی', 'مشخصات احتمالی گوشی‌های سری پیکسل ۶ گوگل پیش از موعد رونمایی فاش شد', 'جواد تاجی ، 18 تیر 1400', 'https://digiato.com/wp-content/uploads/2021/07/Google-Pixel-6-Pro-Leaked-Renders-1-1024x576.png', 'وب‌سایت «Front Page Tech» که پیش از این در اردیبهشت‌ماه اولین تصاویر از گوگل پیکسل ۶ را به اشتراک گذاشته بود، مشخصات کامل‌تری از اعضای این خانواده را به فاش کرده است.\r\n\r\nاین منبع همچنین ادعا می‌کند که نسخه پایه و پرو این خانواده به ترتیب با اسم رمز «Oriole» و «Raven» شناخته می‌شوند و احتمالا در بازه زمانی ۹ مهر تا ۹ آبان از راه می‌رسند.\r\n\r\nگفتنی است تاکنون خبرهای غیررسمی مختلفی در مورد گوشی‌های سری «پیکسل ۶» گوگل منتشر شده است ولی این وب‌سایت نسبت به اطلاعات قبلی، مشخصات کامل‌تری را از اعضای جدید خانواده پبکسل ۶ منتشر کرده است.', 'https://digiato.com/wp-content/uploads/2021/07/photo_2021-05-14_09-12-03.jpg', 'ظاهرا پیکسل ۶ از نمایشگر ۶.۴ اینچی امولد بهره خواهد برد و چیپست اختصاصی GS۱۰۱ گوگل با ۸ گیگابایت رم همراه می‌شود و خریداران می‌توانند از بین گزینه‌های ۱۲۸ یا ۲۵۶ گیگابایتی برای فضای ذخیره‌سازی، یکی را انتخاب کنند. در قسمت دوربین‌ها نیز این دستگاه مجهز به دوربین واید ۵۰ مگاپیکسلی، دوربین اولترا واید ۱۲ مگاپیکسلی و دوربین سلفی ۸ مگاپیکسلی خواهد بود. به علاوه دستگاه مذکور با باتری ۴۶۱۴ میلی آمپر ساعتی و نسخه سیستم عامل اندروید ۱۲ عرضه خواهد شد.\r\n\r\nپیکسل ۶ پرو نیز احتمالا با نمایشگر ۶.۷۱ اینچی اولد پلاستیکی (P-OLED)، به همراه ۱۲ گیگابایت رم و ۱۲۸، ۲۵۶ یا ۵۱۲ گیگابایت فضای ذخیره سازی در دسترس کاربران قرار خواهد گرفت. این گوشی هوشمند نیز از چیپست اختصاصی گوگل بهره می‌برد و ماژول دوربین آن مجهز به دوربین واید ۵۰ مگاپیکسلی، دوربین اولترا واید ۱۲ مگاپیکسلی و دوربین تله‌فوتو ۴۸ مگاپیکسلی خواهد بود. ظاهرا نسخه پرو با باتری با ظرفیت بالاتر ۵۰۰۰ میلی آمپر ساعتی به بازار عرضه خواهد شد.', 'https://fdn2.gsmarena.com/vv/pics/google/google-pixel-6-pro-r1.jpg', 'همچنین در این گزارش عنوان شده است که سری پیکسل ۶ حداقل ۵ سال پشتیبانی نرم‌افزاری را دریافت خواهند کرد. البته هنوز مشخص نشده است که پنج بروزرسانی سیستم عامل یا ترکیبی از این بروزرسانی به همراه آپدیت امنیتی منتشر می‌شود یا برنامه دیگری را دنبال خواهد کرد. با این حال انتظار می‌رود حداقل سه یا چهار مورد بروزرسانی سیستم عامل برای این گوشی منتشر شود.\r\n\r\nسرانجام در حالی که هنوز تاریخ عرضه رسمی این سری مشخص نشده است، این منبع فاش کرد که سری پیکسل ۶ ممکن است در بازه زمانی ۹ مهر تا ۹ آبان از راه برسد.'),
('OnePlus Nord 2', 'موبایل', 'وان پلاس نورد ۲ با تراشه دیمنسیتی ۱۲۰۰ مدیاتک ۳۱ تیرماه از راه می‌رسد', 'جواد تاجی ، 18 تیر 1400', 'https://digiato.com/wp-content/uploads/2021/07/oneplus-nord-2-1-1-1024x660.jpg', 'پیش از این یکی از منابع آگاه بازار از احتمال معرفی وان پلاس نورد ۲ در دوم مردادماه (برابر با ۲۴ ژوئیه) خبر داده بود و اکنون وان پلاس به صورت رسمی تایید کرده که این گوشی هوشمند در تاریخ ۳۱ تیر ۱۴۰۰ رونمایی می‌شود.\r\n\r\nدر پوستر ارائه شده از سوی وان پلاس که زمان معرفی گوشی نورد ۲ را مشخص می‌کند، جعبه این دستگاه نیز به رنگ سیاه دیده می‌شود.', 'https://digiato.com/wp-content/uploads/2021/07/photo_2021-07-08_18-59-03.jpg', 'احتمالا نسل جدید سری نورد از نمایشگر ۶.۴۳ اینچی امولد با رزولوشن فول‌ اچ‌دی پلاس و نرخ تازه سازی ۹۰ هرتز، پردازنده مدیاتک دیمنسیتی ۱۲۰۰، روزنه مخصوص دوربین سلفی روی نمایشگر و حسگر اثر انگشت زیر صفحه نمایش بهره خواهد برد. با وجودی که هنوز به صورت رسمی مشخصات این دستگاه تایید نشده، اما انتظار می‌رود نورد ۲ با حداکثر ۱۲ گیگابایت رم و ۲۵۶ گیگابایت حافظه داخلی در دسترس کاربران قرار گیرد.', 'https://fdn.gsmarena.com/imgroot/news/21/06/oneplus-nord2-leaked-renders/-315x350/gsmarena_001.jpg', 'وان پلاس گوشی هوشمند جدیدش را با بهره‌مندی از دوربین سه‌گانه شامل دوربین واید ۵۰ مگاپیکسلی IMX۷۶۶، دوربین فوق عریض ۸ مگاپیکسلی و دوربین ۲ مگاپیکسلی در کنار دوربین سلفی ۳۲ مگاپیکسلی به بازار عرضه خواهد کرد.\r\n\r\nدر نهایت انتظار می‌رود وان پلاس نورد ۲ با باتری ۴۵۰۰ میلی‌آمپر ساعتی و پشتیبانی از فناوری شارژ سریع Warp Charge 30T پلاس در دسترس کاربران قرار گیرد.\r\n\r\nهنوز طراحی ظاهری این گوشی به صورت دقیق مشخص نشده است اما انتظار می‌رود تا حدی شبیه «وان پلاس ۹» باشد. با این حال تا زمان رونمایی رسمی از نسل جدید سری نورد دو هفته دیگر باقی مانده است و برای اطلاعات بیشتر از این دستگاه باید منتظر بمانیم.'),
('Huawei Under Display camera', 'تکنولوژی', 'هواوی نیز روی توسعه موبایلی با دوربین سلفی زیر نمایشگر کار می‌کند', 'جواد تاجی ، 18  تیر 1400', 'https://digiato.com/wp-content/uploads/2020/12/xiaomi-under-display-camera.jpg', 'به نظر می‌رسد پس از شرکت‌های تولیدکننده مختلفی مانند شیائومی، هواوی نیز قصد دارد در گوشی‌های آینده خود از فناوری دوربین سلفی زیر نمایشگر استفاده کند.\r\n\r\nدر حال حاضر ZTE اولین شرکتی است که دستگاهی با استفاده از فناوری دوربین سلفی زیر نمایشگر به بازار عرضه کرده است و هم اکنون در حال تولید نسل دوم فناوری خود است که از تراکم پیکسلی دو برابر بیشتر از نسل قبل بهره می‌برد. انتظار می‌رود گوشی «سامسونگ گلکسی فولد۳» نیز از این فناوری بهره ببرد و اوپو و شیائومی نیز در حال کار روی گوشی‌های جدیدی با استفاده از این فناوری هستند. اکنون به نظر می‌رسد هواوی نیز در حال توسعه گوشی جدیدی با استفاده از این فناوری است.', 'https://digiato.com/wp-content/uploads/2021/07/gsmarena_002-4.jpg', 'مستندات رسمی پتنت جدید هواوی به همراه تصاویر آن توسط LetsGoDigital به اشتراک گذاشته شده که طراحی گوشی هواوی با استفاده از دوربین زیر نمایشگر را نشان می‌دهد.\r\n\r\nبراساس مستندات منتشر شده، این گوشی هواوی احتمالا میان‌رده خواهد بود و ظاهرا ماژول دوربین و طراحی کلی آن شباهت زیادی به گوشی «آنر X۲۰ SE» دارد. اما باید توجه کرد که تصاویر ارائه شده در پتنت‌ها به هیچ وجه طراحی نهایی یک محصول را نشان نمی‌دهند، بنابراین طراحی گوشی آینده هواوی با استفاده از فناوری دوربین سلفی زیر نمایشگر ممکن است تفاوت زیادی با محصول نهایی ممکن است داشته باشد.\r\n\r\nبا این حال در قسمت جلویی این دستگاه، نمایشگر بزرگی با لبه‌های خمیده و بدون روزنه مخصوص دوربین سلفی دیده می‌شود. روی پنل پشتی گوشی نیز ماژول دوربین سه‌گانه دیده می‌شود که البته سنسورها در یکی از گوشی‌ها در وسط ماژول و در گوشی دیگر به شکل عجیب و ناموزونی در سمت چپ قرار گرفته‌اند. در اطراف آن نیز دکمه‌های تنظیم صدا و پاور دیده می‌شود و به نظر می‌رسد سنسور اثر انگشت روی دکمه پاور قرار گرفته باشد.\r\n\r\nپتنت جدید هواوی در اداره ملی مالکیت معنوی چین (CNIPA) ثبت شده است که این موضوع تعجب‌آور نیست، زیرا این روزها با وجود تحریم‌ها و ممنوعیت‌های اعمال شده، چین تنها بازار موجود محصولات این شرکت را تشکیل می‌دهد.', 'null', 'null'),
('Samsung M52 5g', 'تکنولوژی', 'گلکسی M52 5G گواهی CCC چین را دریافت کرد؛ ظرفیت باتری مطابق انتظار نیست', 'جواد تاجی ، 19 تیر 1400', 'https://digiato.com/wp-content/uploads/2021/07/photo_2021-07-08_14-16-19-1.jpg', 'با وجودی که انتظار می‌رفت گوشی میان‌رده‌ گلکسی M52 5G سامسونگ حداقل از باتری ۶۰۰۰ میلی آمپر ساعتی بهره ببرد، اما مستندات مربوط به گواهی CCC چین حاکی از آن است که موبایل مذکور ظاهرا با باتری ۴۸۶۰ میلی آمپر ساعت عرضه خواهد شد.\r\n\r\nگوشی گلکسی M52 5G به تازگی گواهینامه CCC چین را دریافت کرده و مستندات مربوطه اطلاعاتی در مورد ظرفیت و تولید‌ کننده باتری دستگاه ارائه می‌دهد. براساس مستندات مربوطه، باتری این گوشی هوشمند توسط سامسونگ SDI ساخته شده و ظرفیت آن ۴۸۶۰ میلی آمپر ساعت (ظرفیت نامی ۵۰۰۰ میلی آمپر ساعت) خواهد بود. با توجه به اینکه گلکسی M51 از باتری ۷۰۰۰ میلی آمپر ساعتی بهره می‌برد، این خبر ممکن است برای کسانی که انتظار داشتند این گوشی میان‌رده از باتری ۶۰۰۰ میلی آمپر ساعت یا بیشتر بهره‌مند شود، ناامید کننده باشد.', 'https://digiato.com/wp-content/uploads/2021/07/galxy-1.jpg', 'همچنین هفته گذشته مشخص شد که گلکسی M52 5G احتمالا با پردازنده قدرتمند و شش نانومتری کوالکام یعنی اسنپدراگون ۷۷۸G که چند هفته پیش به بازار عرضه شد، معرفی می‌شود. پس از آن نیز مشخصات دوربین‌های گوشی قدرتمند و میان‌رده گلکسی M52 5G افشا شد که براساس آن دوربین اصلی ۶۴ مگاپیکسلی، دوربین فوق عریض ۱۲ مگاپیکسلی و حسگر ماکرو ۵ مگاپیکسلی در ماژول چهارگانه این دستگاه قرار خواهند گرفت. هنوز اطلاعاتی در مورد دوربین چهارم موبایل مذکور به اشتراک گذاشته نشده است، اما می‌‌توان انتظار داشت که این موبایل جدید مجهز به حسگر تشخیص عمق ۵ مگاپیکسلی نیز باشد. در پنل جلویی دستگاه نیز احتمالا شاهد دوربین سلفی ۳۲ مگاپیکسلی خواهیم بود.', 'https://digiato.com/wp-content/uploads/2021/07/photo_2021-07-08_14-16-22.jpg', 'می‌توان انتظار داشت که گلکسی M52 5G از نمایشگر تمام صفحه سوپر امولد با نرخ تازه سازی بالا، حسگر اثر انگشت زیر نمایشگر، اندروید ۱۱ و حداقل سرعت شارژ سریع ۲۵ وات برخوردار باشد.\r\n\r\nاطلاعات زیادی در مورد مشخصات گلکسی M52 5G منتشر نشده است. هرچند به نظر می‌رسد که برخی از مشخصات سخت افزاری آن شبیه به گلکسی A52 باشد. در واقع هنوز مشخص نیست موبایل جدید از ویژگی‌هایی مانند اسپیکر استریو، مقاومت در برابر نفوذ آب و گرد غبار و استاندارد IP67 برخوردار خواهد بود یا خیر.'),
('Samsung Z fold 3', 'تکنولوژی', 'جدیدترین رندر گلکسی زد فولد ۳ دوربین سلفی زیر نمایشگر را نشان می‌دهد', 'تینا پورشاهید ، 16 تیر 1400', 'https://digiato.com/wp-content/uploads/2021/07/gsmarena_002-1-2.jpg', 'تاکنون اخبار غیررسمی مختلفی در مورد گوشی تاشو گلکسی زد فولد ۳ منتشر شده و حالا رندر جدیدی از آن منتشر شده که گزارش‌های استفاده از دوربین سلفی زیر نمایشگر در این دستگاه را قوت می‌بخشند.\r\n\r\nحساب کاربری با نام «Ice universe» در توییتر، رندری منتسب به گلکسی زد فولد ۳ به اشتراک گذاشته که طراحی آن را به خوبی نشان می‌دهد. با توجه به رندرُ به نظر می‌رسد سامسونگ تصمیم دارد گوشی تاشو جدید را با دوربین سلفی زیر نمایشگر معرفی و به بازار عرضه کند.\r\n\r\nپیشتر نیز اخبار دیگری از سوی منابع آگاه در مورد احتمال عرضه نسخه ۵G گلکسی زد فولد ۳ با دوربین زیر نمایشگر و بدون بهره‌مندی از روزنه مخصوص دوربین سلفی منتشر شده بود. به نظر می‌رسد سامسونگ قصد دارد نیمه دوم سال جاری میلادی این موبایل تاشو را با دوربین زیر نمایشگر بهبودیافته‌‌ای که نورپذیری بیشتری نسبت به سایر نمونه‌های دوربین زیر نمایشگر بازار دارد، در دسترس کاربران قرار دهد.', 'https://digiato.com/wp-content/uploads/2021/07/gsmarena_001-1-1.jpg', 'نسخه ۵G گلکسی زد فولد ۳ قرار است ۲۰ مردادماه (‌یازدهم آگوست) با بهره‌مندی از چیپست اسنپدراگون ۸۸۸ و رم ۱۲ گیگابایتی در رنگ‌های سبز، مشکی و سفید مایل به صورتی معرفی شود. این گوشی هوشمند با نمایشگر تاشو ۷.۵۵ اینچی و نمایشگر خارجی ۶.۲۳ اینچی با نرخ تازه سازی ۱۲۰ هرتز عرضه خواهد شد.\r\n\r\nبه علاوه قرار است این موبایل تاشو سامسونگ از قلم S Pen نیز پشتیبانی کند تا جای خالی نسل جدید گلکسی نوت که امسال وارد بازار نمی‌شود را به شکلی پر کند. همچنین این دستگاه احتمالا در برابر نفوذ آب مقاوم خواهد بود. انتظار می‌رود موبایل مذکور مجهز به باتری ۴۴۰۰ میلی آمپر ساعتی با شارژ سیمی ۲۵ واتی و فناوری شارژ بی‌سیم ۱۵ وات باشد.', 'null', 'null'),
('Galaxy Tab S8', 'موبایل', 'تبلت غول‌پیکر گلکسی تب S8 اولترا سامسونگ احتمالا اوایل ۲۰۲۲ از راه می‌رسد', 'ایمان صاحبی ، 14 تیر 1400', 'https://digiato.com/wp-content/uploads/2020/10/Digipic-GalaxyTabS7-07.jpg', 'سامسونگ معمولا هر سال خانواده جدیدی از تبلت‌های پرچمدار خود را معرفی می‌کند. ولی ظاهرا امسال شرایط کمی تغییر کرده و برای عرضه تبلت‌های سری گلکسی تب S8 که این بار سه مدل خواهند بود، باید تا اوایل سال آینده میلادی صبر کنیم.\r\n\r\nبر اساس یک گزارش جدید، خانواده گلکسی تب S8 به جای سال جاری میلادی در ۲۰۲۲ عرضه می‌شوند. در حال حاضر پنجره زمانی دقیقی برای عرضه این محصولات در دسترس نیست، اما با توجه به شرایط کمبود جهانی تراشه دور از ذهن نیست که عرضه این خانواده به سال آینده میلادی منتقل شده باشد.\r\n\r\nکمبود تراشه حتی روی عرضه گلکسی S21 FE هم اثر گذاشته و معرفی آن را تا اوایل پاییز به تعویق انداخته است. در هر حال، گفته می‌شود که سری گلکسی تب S8 سه مدل خواهد داشت تا شباهت بیشتری به گوشی‌های پرچمدار سامسونگ پیدا کند.', 'https://digiato.com/wp-content/uploads/2020/10/Digipic-GalaxyTabS7-19.jpg', 'مشخصات سخت‌افزاری گلکسی تب S8 و تب S8 پلاس مشابه گلکسی تب S7 و تب S7 پلاس باقی می‌ماند اما پردازنده جدیدی روی آن‌ها قرار می‌گیرد. سامسونگ تمام تمرکز خود را روی مدل جدید این خانواده یعنی گلکسی تب S8 اولترا قرار داده که قرار است نمایشگری ۱۴.۶ اینچی از نوع امولد و یک باتری ۱۲ هزار میلی آمپر ساعتی داشته باشد.\r\n\r\nهر سه عضو خانواده آتی تبلت‌های پرچمدار سامسونگ از قلم هوشمند S Pen و یک کیبورد جانبی پریمیوم تازه پشتیبانی خواهند کرد. این محصولات می‌توانند به شبکه‌های ارتباطی ۵G وصل شوند ولی در مدل‌های وای‌فای و LTE هم به بازار می‌آیند.\r\n\r\nطبق اطلاعات موجود، قیمت این سه مدل از ضعیف به قوی به ترتیب از ۷۴۰، ۱۱۲۰ و ۱۳۲۰ دلار آغاز می‌شود. البته این قیمت‌ها بر اساس قیمت افشا شده برای بازار کره تبدیل شده‌اند و ممکن است قیمت نهایی برای سایر بازارها متفاوت باشد. از آن‌جایی که احتمالا زمان زیادی تا معرفی این دستگاه‌ها باقی مانده، باید منتظر گزارش‌های بیشتری در این زمینه باشیم.', 'null', 'null'),
('OnePlus 9 & 9 pro', 'تکنولوژی', 'حذف وان پلاس ۹ و ۹ پرو از پایگاه داده بنچمارک گیک‌بنچ به اتهام تقلب', 'تینا پورشاهید ، 16 تیر 1400', 'https://digiato.com/wp-content/uploads/2021/07/Oneplus-9-9-Pro.jpg', 'گیک‌بنچ داده‌های مربوط به بنچمارک گوشی‌های هوشمند وان پلاس ۹ و وان پلاس ۹ پرو را به اتهام دستکاری در نتایج و استفاده از برنامه های مخدوش کننده نتایج عملکرد حذف کرد.\r\n\r\nپس از بررسی‌های طولانی و مفصل ادعا شده که وان پلاس در گوشی‌های سری وان پلاس ۹ از برخی برنامه‌های بهینه‌سازی استفاده کرده است که می‌تواند روی عملکرد دستگاه تاثیرگذار باشد. بنچمارک استفاده از چنین اپ‌هایی را نوعی تقلب می‌داند و چون در زمان تست دستگاه‌ها چنین برنامه‌هایی فعال بوده، اکنون گیک‌بنچ مدعی است نتایج تست‌ها قابل اتکا نیستند.\r\n\r\nبراساس قوانین بنچمارک گیک‌بنچ، استفاده از چنین برنامه‌هایی نوعی دستکاری در عملکرد دستگاه محسوب می‌شود و نتایج تست‌هایی که در این شرایط گرفته شده را مخدوش می‌داند. با توجه به بررسی‌های انجام شده، وان پلاس ۹ و نسخه پرو آن از چنین برنامه‌هایی در زمان تست استفاده کرده‌اند.\r\n\r\nگیک‌بنچ قصد دارد سایر گوشی‌های هوشمند وان پلاس را نیز مورد بررسی قرار دهد تا همین موضوع را در مورد آنها ارزیابی کند. پیش از این نیز در آزمون‌های گیک‌بنچ برخی تولیدکنندگان موبایل با روش‌هایی توانسته بودند هنگام اجرای تست، روی برخی مولفه‌هایی که مورد بررسی قرار می‌گیرند، دست ببرند و نتایج را مخدوش کنند.', 'https://digiato.com/wp-content/uploads/2021/07/oneplus-9.jpg', 'باید دید وان پلاس چه پاسخی به اتهام مطرح شده می‌دهد. تاکنون و از زمان حذف شدن این دو گوشی هوشمند از پایگاه داده گیک‌بنچ، وان پلاس هنوز  پاسخی به اتهام مطرح شده نداده است.\r\n\r\nوان پلاس ۹ و نسخه پرو آن سوم فروردین‌ماه معرفی شدند. پرچمدار وان پلاس به نمایشگر امولد ۶.۵۵ اینچی با رزولوشن فول اچ دی پلاس مجهز است که دارای رفرش ریت ۱۲۰ هرتز است. درون حفره نمایشگر این گوشی هوشمند دوربین سلفی ۱۶ مگاپیکسلی قرار دارد. این دستگاه ۸.۷ میلی‌متر ضخامت و ۱۹۲ گرم وزن دارد.\r\n\r\nوان پلاس ۹ پرو نیز نسخه پیشرفته‌تر وان پلاس ۹ محسوب می‌شود که نمایشگر بزرگتری دارد. رزولوشن صفحه نمایش LTPO با ابعاد ۶.۷ اینچی این دستگاه برابر +QHD است و رفرش ریت